import React, { useEffect, useState, useContext } from 'react'

import axios from 'axios'
import { Context } from '../pages/Store'
//import Post from '../Post/Post'
//import './Blog.css'

const Blog = () => {
  const [state, dispatch] = useContext(Context)
  const [data, setData] = useState({ hits: [] })

  useEffect(() => {
    axios
      .get('https://ws.med.tn/web/doctorInfos/?api_key=drmateri')
      .then(response => {
        setData(response.data)
        console.log('88888888888', data)
        //dispatch({ type: 'SET_POSTS', payload: { ...data[0] } })
      })
      .catch(error => {
        console.log('88888888888', data)
        dispatch({ type: 'SET_ERROR', payload: error })
      })
  }, [])

  let posts = <p>Loading...</p>

  if (state.error) {
    posts = (
      <p>
        Something went wrong: <span>{state.error}</span>
      </p>
    )
  }

  if (!state.error && state.posts) {
    posts = state.posts.map(post => {
      return <Post key={post.id} title={post.title} author={post.author} />
    })
  }

  return { posts }

  return {}
}

export default Blog
